import React, { } from "react";
import { BrowserRouter as Router, Switch, Route, Redirect } from "react-router-dom";
import TopBar from './components/TopBar';
function App() {
  const [auth, setAuth] = React.useState({ username: "", password: "" })
  const connected = () => {
    return auth.username === "achraf" && auth.password === "achraf"
  }
  setInterval(() => {
    setAuth({
      username: 'achraf', password: "achraf"
    })
  }, 4500)
  const openSidebar = () => {
      alert('')
  }
  return (
    <Router>
      <TopBar auth={connected} openSidebar={openSidebar} />
      <Switch>
        <Route path="/" exact>
          <div className="app">
            <h1>
              <center>
                {connected() ? "Home" : <Redirect to="/login" />}
              </center>
            </h1>
          </div>
        </Route>
        <Route path="/login" exact>
          <div className="app">
            <h1>
              <center>
                {connected() ? <Redirect to="/" /> : "Login"}
              </center>
            </h1>
          </div>
        </Route>
        <Route path="/register" exact>
          <div className="app">
            <h1>
              <center>
                {connected() ? <Redirect to="/" /> : "Register"}
              </center>
            </h1>
          </div>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
