import React, { useEffect } from "react";
import { BrowserRouter as Router, Switch, Route, useHistory } from "react-router-dom";
import TopBar from './components/TopBar';
function App() {
  const [auth, setAuth] = React.useState({ username: "", password: "" })
  const connected = () => {
    return auth.username === "achraf" && auth.password === "achraf"
  }
  const history = useHistory()
  useEffect(() => {
    if (auth.username !== "achraf" | auth.password !== "achraf")
    {
      history.push('/')
      }
  }, [auth])
  return (
    <Router>
      <TopBar auth={connected} />
      <Switch>
        <Route path="/" exact>
          <div className="app">
            <h1>
              <center>
                Home
              </center>
            </h1>
          </div>
        </Route>
        <Route path="/login" exact>
          <div className="app">
            <h1>
              <center>
                Login
              </center>
            </h1>
          </div>
        </Route>
        <Route path="/register" exact>
          <div className="app">
            <h1>
              <center>
                Register
              </center>
            </h1>
          </div>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
